import React, { useEffect, useState } from 'react';
import './App.css';

type ProductType = "gloves" | "beanies" | "facemasks";


interface Product {
  "id": string;
  "type": ProductType;
  "name": string;
  "color": [];
  "price": number;
  "manufacturer": "hennex" | "ippal" | "juuran" | "niksleh" | "abiplos" | "umpante";
}

function App() {
  const [products, setProducs] = useState<{
    "gloves"?: Product[];
    "beanies"?: Product[];
    "facemasks"?: Product[];
  }>({});
  const [gloves, setGloves] = useState<Product[] | null>(null);
  const [beanies, setBeanies] = useState<Product[] | null>(null);
  const [facemasks, setFacemasks] = useState<Product[] | null>(null);

  const fetchProduct = async (type: ProductType) => {
    const productRes = await fetch(`https://bad-api-assignment.reaktor.com/v2/products/${type}`);
    const productJson: Product[] = await productRes.json();
    setGloves((prevState) => ({
      ...prevState,
      [type]: productJson
    }));
  }

  useEffect(() => {
    fetchGloves();
  }, []);

  return (
    <div className="App">
      <div className="content">
        <div onClick={() => fetchGloves()}>{'Gloves'}</div>
        <div onClick={() => fetchBeanies()}>{'Beanies'}</div>
        <div onClick={() => fetchFacemasks()}>{'Face masks'}</div>
      </div>
    </div>
  );
}

export default App;
