import React, { useEffect, useState } from 'react';
import './App.css';

type ProductType = "gloves" | "beanies" | "facemasks";

interface Product {
  "id": string;
  "type": ProductType;
  "name": string;
  "color": [];
  "price": number;
  // Tarkista puuttuuko tästä jotain, varmaan puuttuu
  "manufacturer": "hennex" | "ippal" | "juuran" | "niksleh" | "abiplos" | "umpante";
}

function App() {
  const [products, setProducts] = useState<{
    "gloves"?: Product[];
    "beanies"?: Product[];
    "facemasks"?: Product[];
  }>({});
  const [selectedProductList, setSelectedProductList] = useState<ProductType>("gloves");

  const fetchProduct = async (type: ProductType) => {
    const productRes = await fetch(`https://bad-api-assignment.reaktor.com/v2/products/${type}`);
    const productJson: Product[] = await productRes.json();
    setProducts((prevState) => ({
      ...prevState,
      [type]: productJson
    }));
  }

  const handleProductLinkClick = (type: ProductType) => {
    if (!products[type]) {
      fetchProduct(type);
    }
  }

  useEffect(() => {
    fetchProduct("gloves");
  }, []);

  return (
    <div className="App">
      <div className="content">
        <div onClick={() => fetchGloves()}>{'Gloves'}</div>
        <div onClick={() => fetchBeanies()}>{'Beanies'}</div>
        <div onClick={() => fetchFacemasks()}>{'Face masks'}</div>
      </div>
    </div>
  );
}

export default App;
